import React, { useEffect, useState } from 'react'
import { FaPython } from 'react-icons/fa'
import { database } from '../firebase'


const generaSemana = (i) => {


    const inicio = i.clone().startOf('day')

    let s=[]
    for(let c=0;c!=6;c++){
        console.log(i.format("dddd"))
        s.push({
            i:inicio.clone().add("days",c),
            nombre:i.format("dddd"),
            abbr:i.format("dddd").charAt(0),
            compis:[],
            errores:[]
        })
    }
    return s
}




export const HorarioEditorContext = React.createContext();




export function useHorarioEditorContext() {
    return React.useContext(HorarioEditorContext)
}

export function HorarioEditorProvider(props) {
    const { children, i } = props
    const [incio, setInicio] = useState(i)
    const [semana, setSemana] = useState(generaSemana(i))
    const [tramoHorario, setTramoHorario] = useState()

    useEffect(() => {
        console.log("Contexto de crear horario creado " + i.format("MMM dd"))
    }, [])

    const value = {
        incio,
        tramoHorario,

        setTramoHorario

    }


    return (
        <HorarioEditorContext.Provider value={value}>

            {children}

        </HorarioEditorContext.Provider>
    )
}
