import React, { useRef, useState } from 'react'
import { Button, Col, Row } from 'react-bootstrap'
import { FaMoon, FaSun } from 'react-icons/fa'
import { useAuth } from '../../contexts/AuthContext'
import moment from 'moment'
import { HorarioEditorProvider, useHorarioEditorContext } from '../../contexts/HorarioEditorContext'


let primerLunesDelMes = moment().year(2020).month(10).date(1).day(8);
if (primerLunesDelMes.date() > 7)
    primerLunesDelMes.day(-6);


const DiaHolder = (props) => {

    const { children } = props
    const { nombre, abbr, horas, compis } = props
    console.log(props)
    return (
        <Col
            className="text-white p-0 d-flex justify-content-center align-items-start border h-100 position-relative"
            style={{ overflowY: "scroll" }}
            onCLick={() => { console.log("dia vacio solo agregrar") }}
        >
            <div className="py-2 d-block d-lg-flex h-100 p-0" onClick={() => { console.log("focus al dia ") }} >
                {children}
            </div>
            <div className=" bg-info p-0 w-100 text-center border-top border-white shadow" style={{position:"fixed",bottom:"0"}} > 3 / 6 </div>
        </Col>
    )
}


const DiaBarraaHolder = ({ children }) => {

    return (
        <Col className="text-white p-0 d-flex justify-content-center align-items-center border" style={{ maxHeight: "100%", overflowY: "hidden" }}>

            {children}
        </Col>
    )
}


const SemanaHolder = (props) => {

    const { turnoAbr, handleTramoHorario } = props
    const { userAPI } = useAuth()
    const { semana } = useHorarioEditorContext()


    return (
        <Row className="mx-auto h-100">
            <DiaBarraaHolder>{turnoAbr}</DiaBarraaHolder>
            {semana?.dias.map(d =>
                <DiaHolder {...d}>
                    <Col xs={12} sm={10} as={"img"} src={userAPI?.profileUrl} className="shadow rounded-circle  border border-2 border-white mx-auto d-block my-2" />
                    <Col xs={12} sm={10} as={"img"} src={userAPI?.profileUrl} className="shadow rounded-circle  border border-2 border-white mx-auto d-block my-2" />
                    <Col xs={12} sm={10} as={"img"} src={userAPI?.profileUrl} className="shadow rounded-circle  border border-2 border-white mx-auto d-block my-2" />
                    <Col xs={12} sm={10} as={"img"} src={userAPI?.profileUrl} className="shadow rounded-circle  border border-2 border-white mx-auto d-block my-2" />
                    <Col xs={12} sm={10} as={"img"} src={userAPI?.profileUrl} className="shadow rounded-circle  border border-2 border-white mx-auto d-block my-2" />
                    <Col xs={12} sm={10} as={"img"} src={userAPI?.profileUrl} className="shadow rounded-circle  border border-2 border-white mx-auto d-block my-2" />
                </DiaHolder>
            )}
        </Row >
    )


}



const BarraDias = (props) => {
    return (
        <Row className="bg-info text-white p-0 m-0" style={{ height: "5%" }}>
            <DiaBarraaHolder><FaSun />-<FaMoon /></DiaBarraaHolder>
            <DiaBarraaHolder>L</DiaBarraaHolder>
            <DiaBarraaHolder>M</DiaBarraaHolder>
            <DiaBarraaHolder>X</DiaBarraaHolder>
            <DiaBarraaHolder>J</DiaBarraaHolder>
            <DiaBarraaHolder>V</DiaBarraaHolder>
            <DiaBarraaHolder>S</DiaBarraaHolder>
            <DiaBarraaHolder>D</DiaBarraaHolder>
        </Row>
    )
}


const SemanaPasada = (props) => {

    const [tramoActivo, setTramoActivo] = useState()

    return (
        <div className="bg-warning w-100 mx-auto m-0 p-0" style={{ height: "20%" }}>
            <div className="h-50 bg-dark">

            </div>
            <div className="h-50 bg-warning">

            </div>
        </div>
    )
}


export default (props) => {
    const semanaPasada = useRef()
    const apertura = "09:30"
    const cambioTurno = "17:00"
    const cierre = "00:00"
    return (
        <HorarioEditorProvider i={primerLunesDelMes}>
            <div className="vh-100 vw-100 bg-info p-0 m-0">
                <SemanaPasada ref={semanaPasada} />
                <BarraDias />
                <div className="w-100 m-0 p-0" style={{ height: "65%" }}>
                    <div className="bg-claro-oscuro h-50 p-0 mx-auto w-100">
                        <SemanaHolder turnoAbr="M" tramo={{ i: apertura, f: cambioTurno }} />
                    </div>
                    <div className="bg-oscuro h-50">
                        <SemanaHolder turnoAbr="N" tramo={{ i: cambioTurno, f: cierre }} />
                    </div>
                </div>
                <div className="bg-dark w-100 mx-auto m-0 p-0" style={{ height: "10%" }}>

                </div>
            </div>
        </HorarioEditorProvider>

    )
}
