import React, { createRef, forwardRef, useEffect, useImperativeHandle, useRef } from 'react'
import MainHolder from "../components/common/MainHolder";
import { Row, Form, Col, Button } from "react-bootstrap";

import moment from 'moment';






const FormularioTrabajador = forwardRef((props, ref) => {


    const fotoHolder = useRef()
    const nombreInput = useRef()
    const correoInput = useRef()


    useEffect(() => {
        console.log(fotoHolder)
    }, [fotoHolder])


    useImperativeHandle(ref, () => ({

        getData() {

            const foto = fotoHolder.current?.toDataURL('image/webp', 0.92)

            return { nombre: nombreInput.current?.value, correo: correoInput.current?.value, foto }
        }

    }));


    const drawImage = (ev) => {
        console.log("archivo cambiado")
        const canvas = fotoHolder.current
        if (canvas) {
            const f = ev.target.files[0]
            var img = new Image()
            img.src = URL.createObjectURL(f)
            img.onload = () => {
                const cw = canvas.width
                const ch = canvas.height
                const context = canvas.getContext("2d")
                context.clearRect(0, 0, cw, ch);
                context.drawImage(img, 0, 0, img.width, img.height,     // source rectangle
                    0, 0, cw, ch);

                context.beginPath();
                context.arc(cw/2, ch/2, (cw+ch)/4 , 0, 2 * Math.PI, false);
                context.fillStyle = 'green';
                context.fill();
                context.lineWidth = 5;
                context.strokeStyle = '#003300';
                context.stroke();
            }
        }

    }


    return (
        <Form as={Row} className="py-2 m-0 p-0" ref={ref}>

            <Form.Label as={Col} xs={12} className="my-4 ps-2" >
                Informacion del trabajador
            </Form.Label>
            <Row className="border border-warning py-2 m-0 p-0" style={{ width: "100%", overflow: "hidden" }}>
                <canvas className="mx-auto bg-info p-0" style={{ width: '250px', height: '250px' }} ref={fotoHolder} />

                <Form.Group controlId="formFile" className="mb-3" as={Col} xs={12}>
                    <Form.Label>Foto</Form.Label>
                    <Form.Control type="file" accept="image/*" size="sm" onChange={drawImage} />
                </Form.Group>
            </Row>

            <Form.Group>
                <Form.Label >
                    Nombre
                </Form.Label>
                <Form.Control ref={nombreInput} />
            </Form.Group>
            <Form.Group as={Col} xs={12}>
                <Form.Label >
                    Correo usuario
                </Form.Label>
                <Form.Control ref={correoInput} />
            </Form.Group>
        </Form>
    )
})


const FormularioContrato = forwardRef((props, ref) => {
    return (
        <Form as={Row} className="mx-auto" ref={ref}>
            <Form.Label as={Col} xs={12} className="ps-2 my-4">
                Contrato del trabajador
            </Form.Label>
            <Form.Group>
                <div className="mb-3 d-flex justify-content-center">
                    <Form.Check type="checkbox">
                        <Form.Check.Input type="checkbox" />
                        <Form.Check.Label className="ms-2">Trabajador indefinido</Form.Check.Label>

                    </Form.Check>
                </div>

            </Form.Group>

            <Form.Group as={Col} xs={12}>
                <Form.Label >
                    Inicio contrato
                </Form.Label>
                <Form.Control type="date" />
            </Form.Group>

            <Form.Group as={Col} xs={12}>
                <Form.Label >
                    Fin contrato
                </Form.Label>
                <Form.Control type="date" />
            </Form.Group>

            <Form.Group as={Col} xs={12}>
                <Form.Label >
                    Horas de trabajo
                </Form.Label>
                <Form.Control type="number" />
            </Form.Group>
            <Form.Group as={Col} xs={6}>
                <Form.Label >
                    Dias laborables
                </Form.Label>
                <Form.Control type="number" defaultValue={5} />
            </Form.Group>
            <Form.Group as={Col} xs={6}>
                <Form.Label >
                    Dias descanso
                </Form.Label>
                <Form.Control type="number" defaultValue={2} />
            </Form.Group>
            <Form.Group as={Col} xs={12}>
                <Form.Label >
                    Duracion vacaciones (dias)
                </Form.Label>
                <Form.Control type="number" defaultValue={30} />
            </Form.Group>

            <Form.Group as={Col} xs={12}>
                <Form.Label >
                    Sueldo (€)
                </Form.Label>
                <Form.Control type="number" />
            </Form.Group>

            <Form.Group as={Col} xs={12}>
                <Form.Label >
                    Seguros sociales (€)
                </Form.Label>
                <Form.Control type="number" />
            </Form.Group>


        </Form>
    )
})


const PlantillaCreator = (props) => {




    const trabajador = useRef()



    const handleSubmit = (ev) => {
        trabajador.current?.getData()
        console.log("enviando formulario")
    }

    useEffect(() => {
        console.log(trabajador)
    }, [trabajador])


    return (
        <MainHolder>


            <Form as={Row} className="bg-dark text-white mx-auto py-2" onSubmit={handleSubmit}>

                <FormularioTrabajador ref={trabajador} />


                <FormularioContrato />

                <Button className="w-100 mt-2 bg-success" onClick={handleSubmit} value="tu_culo">Crear</Button>

            </Form>
        </MainHolder>
    )
}

export default PlantillaCreator
