import React from "react";
import { Col, Row, Button, ButtonGroup } from "react-bootstrap";
import MainHolder from "../components/common/MainHolder";
import { useCompis } from "../contexts/CompisContext";
import { useTrabajador } from "../contexts/TrabajadorContext";
import { useContrato } from "../contexts/ContratoContext";
import { Link } from 'react-router-dom';
import moment from 'moment'
import { FaEdit } from "react-icons/fa";


const ProfilePic = (props) => {
    const { profile_pic, nombre } = props
    return (
        <Col xs={3} className="bg-dark p-0 ">
            {
                profile_pic ?
                    <img src={profile_pic} className="w-100" />
                    :
                    <p className="fs-4">{nombre.substring(0, 3)}</p>
            }
        </Col>
    )
}

const Contrato = (props) => {
    const { horas, inicio, duracion, sueldo, ss, jornada, vacaciones } = props
    return (
        <Row className="bg-info mx-auto p-0">
            <Col xs={4} className="d-flex justify-content-center align-items-center">
                <span className="fs-3 text-white">{horas}</span>
            </Col>
            <Col xs={8} className="p-0 text-center">

                {duracion ?
                    <Row className="mx-auto bg-success p-0">
                        <Col className="text-center">{inicio.format("DD-MM-YYYY")}</Col>
                        <Col className="text-center">{inicio.substract(duracion, "months")}</Col>
                    </Row>

                    :
                    "Indefinido"
                }

            </Col>
        </Row>
    )

}


const Trabajador = (props) => {

    const { nombre, correo, id } = props
    const { getCompiByCorreo } = useCompis()
    const { getContratoByTrabajadorID, contratos } = useContrato()

    const contrato = getContratoByTrabajadorID(id)
    console.log(contratos)
    return (
        <Col className="bg-warning mx-auto p-0" xs={12} md={6}>
            <Row className="mx-auto p-0">
                <ProfilePic nombre={nombre} profile_pic={getCompiByCorreo(correo)?.pic_url} />
                <Col className="bg-dark text-white fs-4 pt-2 text-capitalize">
                    {nombre}
                    {contrato && <Contrato {...contrato} />
                    }
                </Col>
                <Col xs={12} className="bg-info border-top p-0 d-flex justify-content-center">
                    <ButtonGroup>
                        <Button variant="secondary">Baja</Button>
                        <Button variant="secondary">Vacaciones</Button>
                        <Button variant="secondary" >Editar</Button>
                    </ButtonGroup>
                </Col>
            </Row>
        </Col>
    )
}

export default (props) => {
    const { trabajadores } = useTrabajador()
    return (
        <MainHolder>
            <Col className="h-100 bg-white">
                <Row className="bg-info mx-auto px-1" style={{ height: '90%', overflowY: "scroll" }}>
                    {trabajadores?.map(t => <Trabajador {...t} />)}
                </Row>
                <Link className="btn btn-success w-100 d-flex align-items-center justify-content-center" style={{ height: '10%' }} to="/plantilla/nuevo">Nuevo</Link>
            </Col>

        </MainHolder>
    )
}
